# -------------------- –ì–µ–Ω–µ—Ä–∞—Ü–∏—è —Ç–µ–∫—Å—Ç–∞ --------------------
def generate_with_openrouter(api_key, model_name, topic):
    url = f"https://openrouter.ai/api/v1/completion"
    headers = {
        "Authorization": f"Bearer {api_key}",
        "Content-Type": "application/json"
    }
    data = {
        "model": model_name,
        "prompt": f"–ù–∞–ø–∏—à–∏ —Å—Ç–∞—Ç—å—é –Ω–∞ —Ä—É—Å—Å–∫–æ–º —è–∑—ã–∫–µ –ø–æ —Ç–µ–º–µ: {topic}. –î–ª–∏–Ω–∞ —Å—Ç–∞—Ç—å–∏ –Ω–µ –º–µ–Ω–µ–µ 300 —Å–ª–æ–≤.",
        "max_tokens": 1000
    }
    response = requests.post(url, json=data, headers=headers)
    if response.status_code == 200:
        return response.json().get('choices', [{}])[0].get('text', '')
    return f"–û—à–∏–±–∫–∞: {response.status_code}"

def generate_with_groq(api_key, model_name, topic):
    url = f"https://api.groq.ai/v1/complete"
    headers = {
        "Authorization": f"Bearer {api_key}",
        "Content-Type": "application/json"
    }
    data = {
        "model": model_name,
        "prompt": f"Write an article in Russian on the topic: {topic}. The article should be at least 300 words long.",
        "max_tokens": 1000
    }
    response = requests.post(url, json=data, headers=headers)
    if response.status_code == 200:
        return response.json().get('choices', [{}])[0].get('text', '')
    return f"–û—à–∏–±–∫–∞: {response.status_code}"

